import React, { useState } from "react";

function App() {
  const [location, setLocation] = useState("");
  const [budget, setBudget] = useState("");
  const [days, setDays] = useState("");
  const [preferences, setPreferences] = useState("");
  const [itinerary, setItinerary] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setItinerary("");

    try {
      const response = await fetch("http://localhost:5050/generate-itinerary", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ location, budget, days, preferences }),
      });

      const data = await response.json();
      setItinerary(data.itinerary);
    } catch (error) {
      setItinerary("Error generating itinerary.");
    }

    setLoading(false);
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif" }}>
      <h1>TripEase: AI Travel Planner</h1>
      <form onSubmit={handleSubmit} style={{ marginBottom: "1rem" }}>
        <div>
          <label>Location: </label>
          <input value={location} onChange={(e) => setLocation(e.target.value)} required />
        </div>
        <div>
          <label>Budget: </label>
          <input value={budget} onChange={(e) => setBudget(e.target.value)} required />
        </div>
        <div>
          <label>Number of Days: </label>
          <input value={days} onChange={(e) => setDays(e.target.value)} required />
        </div>
        <div>
          <label>Preferences: </label>
          <input value={preferences} onChange={(e) => setPreferences(e.target.value)} required />
        </div>
        <button type="submit" style={{ marginTop: "1rem" }}>
          Generate Itinerary
        </button>
      </form>

      {loading ? (
        <p>Loading...</p>
      ) : itinerary ? (
        <pre>{itinerary}</pre>
      ) : null}
    </div>
  );
}

export default App;
